import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {



  berechne(kommzeit:string, pausezeit:string){
    let kommzeitSTD: number = 0
    let kommzeitMIN: number = 0
    let pausezeitnum: number = 0
    let error1: boolean = false
    let labelergebnis = document.getElementById("ergblable") as HTMLLabelElement;

    let splitS: string [] = kommzeit.split(":");

    try{
    let kommzeitSTD: number = parseInt(splitS [0])
    let kommzeitMIN: number = parseInt(splitS [1])
    let pausezeitnum: number = parseInt(pausezeit)
    } catch (error){
      labelergebnis.textContent = "Gebe zahl an"
      let error1: boolean = true
    }

    let ergebnisSTD: number = (kommzeitSTD + 7) 
    let ergebnisMIN: number = (kommzeitMIN + 42 + pausezeitnum) 

    if (pausezeitnum < 0 || kommzeitSTD < 0 || kommzeitMIN < 0 || kommzeitSTD > 23 || kommzeitMIN >= 60) {
			labelergebnis.textContent = "Ungültige Zahl"
		} else {

    if (ergebnisMIN >= 0) {
      ergebnisSTD += ergebnisMIN / 60;
      ergebnisMIN = ergebnisMIN % 60;
    }

    if (ergebnisSTD >= 24) {
      ergebnisSTD -= 24;
    }

    labelergebnis.textContent = Math.floor(ergebnisSTD) + ":" + ergebnisMIN;
  }
  }
}
